var gallery = document.querySelector('#gallery');
var getVal = function (elem, style) { return parseInt(window.getComputedStyle(elem).getPropertyValue(style)); };
var getHeight = function (item) { return item.querySelector('.content').getBoundingClientRect().height; };
var resizeAll = function () {
    var altura = getVal(gallery, 'grid-auto-rows');
    var gap = getVal(gallery, 'grid-row-gap');
    gallery.querySelectorAll('.gallery-item').forEach(function (item) {
        var el = item;
        el.style.gridRowEnd = "span " + Math.ceil((getHeight(item) + gap) / (altura + gap));
    });
};

window.addEventListener('resize', resizeAll);
gallery.querySelectorAll('.gallery-item').forEach(function (item) {
    item.addEventListener('click', function () {        
        item.classList.toggle('full');        
    });
});















(function() { // wrap code in IIFE to keep it from global scope
    let links = document.querySelectorAll('ab'); // grab all <a> tags that trigger the image gallery
    let imageContainer = document.querySelector('.gallery-item .content'); // grab the div that will contain the images
    let imagesCollection = document.querySelectorAll('.gallery-item');


  function displayImage(imgs, album) { // function to check the data-album attr and check against the button that was clicked
    imgs.forEach((image) => {
      if(image.dataset.album == album) {
        image.classList.remove('hide');
      } else {
        image.classList.add('hide');
      }
    });
  }
  
    
    links.forEach((link) => { // loop through <a> tags and create click event on each 
        link.addEventListener('click', (e) => {
            e.preventDefault();

            switch (link.textContent) { // check name of link clicked 
                case "Certificates": // link 1 text
                
                    displayImage(imagesCollection, 'Certificates'); //display images from album 1

                    break;
                case "Projects": // link 2 text
                
                    displayImage(imagesCollection, 'Projects'); //display images from album 2

                    break;
                case "Personal": // link 3 text
                
                    displayImage(imagesCollection, 'Personal'); //display images from album 3

                    break;
                    
                case "All": // // link 4 text - display all images at once
                
                        imagesCollection.forEach((image) => {
                          image.classList.remove('hide');
                   });

                    break;
            }

        });
    });
    
    })();







    if ('serviceWorker' in navigator) {
  window.addEventListener('load', function() {
    navigator.serviceWorker.register('/sw.js').then(function(registration) {
      // Registration was successful
      console.log('ServiceWorker registration successful with scope: ', registration.scope);
    }, function(err) {
      // registration failed :(
      console.log('ServiceWorker registration failed: ', err);
    });
  });
}